private static void addTagValues(Set<Material> set, Tag... tags) {
  for (Tag tag : tags) {
    set.addAll(tag.getValues());
  }
}

private static void addMaterialValues(Set<Material> set, Material... materials) {
  Collections.addAll(set, materials);
}

