@Test public void testWithFileDeletion(){
  final Context context=new Context();
  final String VPC_STACK_PREFIX = "vpc-2azs-";
  final String STACK_PREFIX = "s3-virusscan-";
  final String VPC_YAML = "vpc/vpc-2azs.yaml";
  final String TEMPLATE_YAML = "template.yaml";
  final String PARAMETER_KEY = "ParentVPCStack";
  final String OUTPUT_VALUE = "ScanQueueArn";
  final String NO_VIRUS_TXT = "no-virus.txt";
  final String VIRUS_TXT = "virus.txt";
  final String VIRUS_CONTENT = "X5O!P%@AP[4\\PZX54(P^)7CC)7}$EICAR-STANDARD-ANTIVIRUS-TEST-FILE!$H+H*";
  final String vpcStackName= VPC_STACK_PREFIX + this.random8String();
  final String stackName= STACK_PREFIX + this.random8String();
  final String bucketName= STACK_PREFIX + this.random8String();
  try {
    this.createWiddixStack(context,vpcStackName,VPC_YAML);
    try {
      this.createStack(context,stackName,TEMPLATE_YAML,new Parameter().withParameterKey(PARAMETER_KEY).withParameterValue(vpcStackName));
      try {
        this.createBucketWithSQSNotification(bucketName,this.getStackOutputValue(stackName,OUTPUT_VALUE));
        this.createObject(bucketName,NO_VIRUS_TXT,"not a virus");
        this.createObject(bucketName,VIRUS_TXT,VIRUS_CONTENT);
        this.retry(context,() -> {
          if (this.doesObjectExist(bucketName,VIRUS_TXT)) {
            throw new RuntimeException(VIRUS_TXT + " must be deleted");
          }
          return false;
        }
);
        this.retry(context,() -> {
          if (!this.doesObjectExist(bucketName,NO_VIRUS_TXT)) {
            throw new RuntimeException(NO_VIRUS_TXT + " must be existing");
          }
          return true;
        }
);
        this.deleteObject(context,bucketName,NO_VIRUS_TXT);
      }
  finally {
        this.deleteBucket(context,bucketName);
      }
    }
  finally {
      this.deleteStack(context,stackName);
    }
  }
  finally {
    this.deleteStack(context,vpcStackName);
  }
}

