    }
    oneAppenderName=oneAppenderName.trim();
    String oneAsyncAppenderName=oneAppenderName + ASYNC_APPENDERNAME_SUFFIX;
    originalAsyncAppenderNameMap.put(oneAppenderName,oneAsyncAppenderName);
