    String originalAppenderName = originalAsyncAppenderMapEntry.getKey();
    props.setProperty(LOG4J_APPENDER_PREFIX + LOG4J_APPENDER_DELIMITER + asyncAppenderName + LOG4J_APPENDER_DELIMITER + PROP_LOG4J_ORIGINAL_APPENDER_NAME, originalAppenderName);
    this.initialProps.setProperty("batcher." + AsyncAppender.class.getName() + "." + originalAppenderName + "." + "rejectWhenFull", "true");
    String maxThreads = this.initialProps.getProperty("batcher." + AsyncAppender.class.getName() + "." + originalAppenderName + "." + "maxThreads");
    final String DEFAULT_MAX_THREADS = "1";
    if (maxThreads == null) {
      this.initialProps.setProperty("batcher." + AsyncAppender.class.getName() + "." + originalAppenderName + "." + "maxThreads", DEFAULT_MAX_THREADS);
    }
    for (Map.Entry mapEntry : props.entrySet()) {
      String key = mapEntry.getKey().toString();
      if ((key.contains(LOG4J_PREFIX) || key.contains(ROOT_CATEGORY) || key.contains(ROOT_LOGGER)) && !key.contains(PROP_LOG4J_ASYNC_APPENDERS) && !key.contains(PROP_LOG4J_ORIGINAL_APPENDER_NAME)) {
        Optional.ofNullable(mapEntry.getValue()).ifPresent(value -> {
          String[] values = (String.class.cast(value)).split(",");
          StringBuilder valueString = new StringBuilder();
          int ctr = 0;
          for (String oneValue : values) {
            if (oneValue == null) {
              continue;
            }
            ++ctr;
            if (originalAppenderName.equals(oneValue.trim())) {
              oneValue = asyncAppenderName;
            }
            if (ctr != values.length) {
              valueString.append(oneValue).append(",");
            } else {
              valueString.append(oneValue);
            }
          }
          mapEntry.setValue(valueString.toString());
        });
      }
    }
  }
}

