Method_Improvements.Final code:
static byte[] decode(CharSequence in){
  int limit=in.length();
  byte[] out=new byte[(int)(limit * 6L / 8L)];
  int outCount=0;
  int inCount=0;
  int word=0;
  for (int pos=0; pos < limit; pos++) {
    char c=in.charAt(pos);
    int bits;
    switch (c) {
      case 'A': case 'B': case 'C': case 'D': case 'E': case 'F': case 'G': case 'H': case 'I': case 'J': case 'K': case 'L': case 'M': case 'N': case 'O': case 'P': case 'Q': case 'R': case 'S': case 'T': case 'U': case 'V': case 'W': case 'X': case 'Y': case 'Z':
        bits=c - 65;
        break;
      case 'a': case 'b': case 'c': case 'd': case 'e': case 'f': case 'g': case 'h': case 'i': case 'j': case 'k': case 'l': case 'm': case 'n': case 'o': case 'p': case 'q': case 'r': case 's': case 't': case 'u': case 'v': case 'w': case 'x': case 'y': case 'z':
        bits=c - 71;
        break;
      case '0': case '1': case '2': case '3': case '4': case '5': case '6': case '7': case '8': case '9':
        bits=c + 4;
        break;
      case '+': case '-':
        bits=62;
        break;
      case '/': case '_':
        bits=63;
        break;
      case '
': case '': case ' ': case '	':
        continue;
      default:
        throw new IllegalArgumentException("invalid character to decode: " + c);
    }
    word=(word << 6) | (byte)bits & 0xff;
    inCount++;
    if (inCount % 4 == 0) {
      out[outCount++]=(byte)(word >> 16);
      out[outCount++]=(byte)(word >> 8);
      out[outCount++]=(byte)word;
    }
  }
  int lastWordChars=inCount % 4;
  if (lastWordChars == 1) {
    return null;
  }
 else   if (lastWordChars == 2) {
    word=word << 12;
    out[outCount++]=(byte)(word >> 16);
  }
 else   if (lastWordChars == 3) {
    word=word << 6;
    out[outCount++]=(byte)(word >> 16);
    out[outCount++]=(byte)(word >> 8);
  }
  if (outCount == out.length)   return out;
  return Arrays.copyOfRange(out,0,outCount);
}

Old_Method:
static byte[] decode(CharSequence in){
  int limit=in.length();
  for (; limit > 0; limit--) {
    char c=in.charAt(limit - 1);
    if (c != '=' && c != '\n' && c != '\r' && c != ' ' && c != '\t') {
      break;
    }
  }
  byte[] out=new byte[(int)(limit * 6L / 8L)];
  int outCount=0;
  int inCount=0;
  int word=0;
  for (int pos=0; pos < limit; pos++) {
    char c=in.charAt(pos);
    int bits;
    if (c >= 'A' && c <= 'Z') {
      bits=c - 65;
    }
 else     if (c >= 'a' && c <= 'z') {
      bits=c - 71;
    }
 else     if (c >= '0' && c <= '9') {
      bits=c + 4;
    }
 else     if (c == '+' || c == '-') {
      bits=62;
    }
 else     if (c == '/' || c == '_') {
      bits=63;
    }
 else     if (c == '\n' || c == '\r' || c == ' ' || c == '\t') {
      continue;
    }
 else {
      throw new IllegalArgumentException("invalid character to decode: " + c);
    }
    word=(word << 6) | (byte)bits & 0xff;
    inCount++;
    if (inCount % 4 == 0) {
      out[outCount++]=(byte)(word >> 16);
      out[outCount++]=(byte)(word >> 8);
      out[outCount++]=(byte)word;
    }
  }
  int lastWordChars=inCount % 4;
  if (lastWordChars == 1) {
    return null;
  }
 else   if (lastWordChars == 2) {
    word=word << 12;
    out[outCount++]=(byte)(word >> 16);
  }
 else   if (lastWordChars == 3) {
    word=word << 6;
    out[outCount++]=(byte)(word >> 16);
    out[outCount++]=(byte)(word >> 8);
  }
  if (outCount == out.length)   return out;
  return Arrays.copyOfRange(out,0,outCount);
}
