Method_Improvements.Final code:
static byte[] decode(CharSequence in){
  int limit = (int) IntStream.range(0, in.length())
    .filter(i -> {
        char c = in.charAt(i);
        return c != '=' && c != '
' && c != '' && c != ' ' && c != '	';
    })
    .count();
  byte[] out=new byte[(int)(limit * 6L / 8L)];
  int outCount=0, inCount=0, word=0;
  for (int pos=0; pos < limit; pos++) {
    char c=in.charAt(pos);
    int bits;
    switch (c) {
      case 'A' to 'Z':
        bits=c - 65;
        break;
      case 'a' to 'z':
        bits=c - 71;
        break;
      case '0' to '9':
        bits=c + 4;
        break;
      case '+', '-':
        bits=62;
        break;
      case '/', '_':
        bits=63;
        break;
      default:
        continue;
    }
    word=(word << 6) | (byte)bits & 0xff;
    inCount++;
    if (inCount % 4 == 0) {
      out[outCount++]=(byte)(word >> 16);
      out[outCount++]=(byte)(word >> 8);
      out[outCount++]=(byte)word;
    }
  }
  int lastWordChars=inCount % 4;
  if (lastWordChars == 1) return null;
  else if (lastWordChars == 2) {
    word=word << 12;
    out[outCount++]=(byte)(word >> 16);
  } else if (lastWordChars == 3) {
    word=word << 6;
    out[outCount++]=(byte)(word >> 16);
    out[outCount++]=(byte)(word >> 8);
  }
  return outCount == out.length ? out : Arrays.copyOfRange(out,0,outCount);
}

Old_Method:
static byte[] decode(CharSequence in){
  int limit=in.length();
  for (; limit > 0; limit--) {
    char c=in.charAt(limit - 1);
    if (c != '=' && c != '\n' && c != '\r' && c != ' ' && c != '\t') {
      break;
    }
  }
  byte[] out=new byte[(int)(limit * 6L / 8L)];
  int outCount=0;
  int inCount=0;
  int word=0;
  for (int pos=0; pos < limit; pos++) {
    char c=in.charAt(pos);
    int bits;
    if (c >= 'A' && c <= 'Z') {
      bits=c - 65;
    }
 else     if (c >= 'a' && c <= 'z') {
      bits=c - 71;
    }
 else     if (c >= '0' && c <= '9') {
      bits=c + 4;
    }
 else     if (c == '+' || c == '-') {
      bits=62;
    }
 else     if (c == '/' || c == '_') {
      bits=63;
    }
 else     if (c == '\n' || c == '\r' || c == ' ' || c == '\t') {
      continue;
    }
 else {
      throw new IllegalArgumentException("invalid character to decode: " + c);
    }
    word=(word << 6) | (byte)bits & 0xff;
    inCount++;
    if (inCount % 4 == 0) {
      out[outCount++]=(byte)(word >> 16);
      out[outCount++]=(byte)(word >> 8);
      out[outCount++]=(byte)word;
    }
  }
  int lastWordChars=inCount % 4;
  if (lastWordChars == 1) {
    return null;
  }
 else   if (lastWordChars == 2) {
    word=word << 12;
    out[outCount++]=(byte)(word >> 16);
  }
 else   if (lastWordChars == 3) {
    word=word << 6;
    out[outCount++]=(byte)(word >> 16);
    out[outCount++]=(byte)(word >> 8);
  }
  if (outCount == out.length)   return out;
  return Arrays.copyOfRange(out,0,outCount);
}
