/** 
 * Light shift of whole byte array by shiftBitCount bits. This method will alter the input byte array. <p> <strong>Analysis</strong> <ul> <li>Time Complexity: <code>O(n)</code></li> <li>Space Complexity: <code>O(1)</code></li> <li>Alters Parameters: <code>true</code></li> </ul> </p>
 * @param byteArray     to shift
 * @param shiftBitCount how many bits to shift
 * @param byteOrder     endianness of given byte array
 * @return shifted byte array
 */
static byte[] shiftLeft(byte[] byteArray,int shiftBitCount,ByteOrder byteOrder){
  final int shiftMod=shiftBitCount % 8;
  final byte carryMask=(byte)((1 << shiftMod) - 1);
  final int offsetBytes=(shiftBitCount / 8);
  int sourceIndex;
  if (byteOrder == ByteOrder.BIG_ENDIAN) {
    for (int i=0; i < byteArray.length; i++) {
      sourceIndex=i + offsetBytes;
      if (sourceIndex >= byteArray.length) {
        byteArray[i]=0;
      }
 else {
        byte src=byteArray[sourceIndex];
        byte dst=(byte)(src << shiftMod);
        if (sourceIndex + 1 < byteArray.length) {
          dst|=byteArray[sourceIndex + 1] >>> (8 - shiftMod) & carryMask & 0xff;
        }
        byteArray[i]=dst;
      }
    }
  }
 else {
    for (int i=byteArray.length - 1; i >= 0; i--) {
      sourceIndex=i - offsetBytes;
      if (sourceIndex < 0) {
        byteArray[i]=0;
      }
 else {
        byte src=byteArray[sourceIndex];
        byte dst=(byte)(src << shiftMod);
        if (sourceIndex - 1 >= 0) {
          dst|=byteArray[sourceIndex - 1] >>> (8 - shiftMod) & carryMask & 0xff;
        }
        byteArray[i]=dst;
      }
    }
  }
  return byteArray;
}
