public PathItem getRelationshipPath(){
  if (lineage.isEmpty()) {
    throw new IllegalStateException("Root collections don't have relationships");
  }
  PathItem path=new PathItem();
  lineage.stream().forEach(item -> path.addParametersItem(item.getPathParameter()));
  String schemaName=getSchemaName(type);
  ApiResponse okSingularResponse = createApiResponse(schemaName, true);
  ApiResponse okPluralResponse = createApiResponse(schemaName, false);
  ApiResponse okEmptyResponse = new ApiResponse().description("Successful response");
  Type<?> parentClass=lineage.peek().getType();
  RelationshipType relationshipType=dictionary.getRelationshipType(parentClass,name);
  if (relationshipType.isToMany()) {
    if (canRead(parentClass,name) && canRead(type)) {
      addOperationsToPath(path, schemaName, okPluralResponse, okEmptyResponse);
    }
  }
 else {
    if (canRead(parentClass,name) && canRead(type)) {
      addOperationsToPath(path, schemaName, okSingularResponse, okEmptyResponse);
    }
  }
  if (path.getGet() != null) {
    addParametersToPath(path);
  }
  decorateGlobalResponses(path);
  decorateGlobalParameters(path);
  return path;
}

private ApiResponse createApiResponse(String schemaName, boolean isSingular) {
  return new ApiResponse().description("Successful response").content(new Content().addMediaType(JsonApi.MEDIA_TYPE,new MediaType().schema(isSingular ? new Datum(new Relationship(schemaName)) : new Data(new Relationship(schemaName)))));
}

private void addOperationsToPath(PathItem path, String schemaName, ApiResponse response, ApiResponse emptyResponse) {
  // Add the common operations to path
}

private void addParametersToPath(PathItem path) {
  getFilterParameters().forEach(param -> path.getGet().addParametersItem(param));
  getPageParameters().forEach(param -> path.getGet().addParametersItem(param));
}

