@BeforeAll public final void init() throws Exception {
  final Path configRoot=Paths.get(Files.createTempDirectory("test").toFile().getAbsolutePath(),"1","2","3","4","5");
  Files.createDirectories(configRoot);
  final ElideStandaloneTestSettings settings=new ElideStandaloneTestSettings(){
    @Override public EntityDictionary getEntityDictionary(    ServiceLocator injector,    ClassScanner scanner,    Optional<DynamicConfiguration> dynamicConfiguration,    Set<Type<?>> entitiesToExclude){
      Map<String,Class<? extends Check>> checks=new HashMap<>();
      if (getAnalyticProperties().enableDynamicModelConfigAPI()) {
        checks.put(ConfigChecks.CAN_CREATE_CONFIG,ConfigChecks.CanCreate.class);
        checks.put(ConfigChecks.CAN_READ_CONFIG,ConfigChecks.CanRead.class);
        checks.put(ConfigChecks.CAN_DELETE_CONFIG,ConfigChecks.CanDelete.class);
        checks.put(ConfigChecks.CAN_UPDATE_CONFIG,ConfigChecks.CanNotUpdate.class);
      }
      EntityDictionary dictionary=new EntityDictionary(checks,new HashMap<>(),new Injector(){
        @Override public void inject(        Object entity){
          injector.inject(entity);
        }
        @Override public <T>T instantiate(        Class<T> cls){
          return injector.create(cls);
        }
      }
,CoerceUtil::lookup,entitiesToExclude,scanner);
      dynamicConfiguration.map(DynamicConfiguration::getRoles).orElseGet(Collections::emptySet).forEach(role -> dictionary.addRoleCheck(role,new Role.RoleMemberCheck(role)));
      return dictionary;
    }
    @Override public ElideStandaloneAnalyticSettings getAnalyticProperties(){
      return createElideStandaloneAnalyticSettings();
    }
  };
  final ElideStandalone elide=new ElideStandalone(settings);
  elide.start(false);
}

private ElideStandaloneAnalyticSettings createElideStandaloneAnalyticSettings() {
  return new ElideStandaloneAnalyticSettings(){
    @Override public boolean enableDynamicModelConfig(){
      return true;
    }
    @Override public boolean enableDynamicModelConfigAPI(){
      return true;
    }
    @Override public String getDynamicConfigPath(){
      return configRoot.toFile().getAbsolutePath();
    }
    @Override public boolean enableAggregationDataStore(){
      return true;
    }
    @Override public boolean enableMetaDataStore(){
      return true;
    }
  };
}

