/** 
 * Tests Read Permissions on AsyncQuery Model for Admin Role.
 * @throws IOException IOException
 */
@Test public void asyncQueryModelAdminReadPermissions() throws IOException {
  ElideResponse<String> response=null;
  String id="edc4a871-dff2-4054-804e-d80075c08959";
  String query="test-query";
  com.yahoo.elide.async.models.AsyncQuery queryObj=new com.yahoo.elide.async.models.AsyncQuery();
  queryObj.setId(id);
  queryObj.setQuery(query);
  queryObj.setQueryType(QueryType.JSONAPI_V1_0);
  queryObj.setPrincipalName("owner-user");
  dataStore.populateEntityDictionary(EntityDictionary.builder().checks(AsyncIntegrationTestApplicationResourceConfig.MAPPINGS).build());
  DataStoreTransaction tx=dataStore.beginTransaction();
  tx.createObject(queryObj,null);
  tx.commit(null);
  tx.close();
  EntityDictionary entityDictionary=EntityDictionary.builder().checks(AsyncIntegrationTestApplicationResourceConfig.MAPPINGS).build();
  Elide elide=new Elide(ElideSettings.builder().dataStore(dataStore).entityDictionary(entityDictionary).auditLogger(new TestAuditLogger()).settings(JsonApiSettingsBuilder.withDefaults(entityDictionary)).build());
  elide.doScans();
  JsonApi jsonApi=new JsonApi(elide);
  User ownerUser=new User(() -> "owner-user");
  SecurityContextUser securityContextAdminUser=new SecurityContextUser(new SecurityContext(){
    @Override public Principal getUserPrincipal(){
      return () -> "1";
    }
    @Override public boolean isUserInRole(    String s){
      return true;
    }
    @Override public boolean isSecure(){
      return false;
    }
    @Override public String getAuthenticationScheme(){
      return null;
    }
  }
);
  SecurityContextUser securityContextNonAdminUser=new SecurityContextUser(new SecurityContext(){
    @Override public Principal getUserPrincipal(){
      return () -> "2";
    }
    @Override public boolean isUserInRole(    String s){
      return false;
    }
    @Override public boolean isSecure(){
      return false;
    }
    @Override public String getAuthenticationScheme(){
      return null;
    }
  }
);
  String baseUrl="/";
  Route route=Route.builder().baseUrl(baseUrl).path("/asyncQuery/" + id).apiVersion(NO_VERSION).build();
  response=jsonApi.get(route,ownerUser,null);
  assertEquals(HttpStatus.SC_OK,response.getStatus());
  response=jsonApi.get(route,securityContextAdminUser,null);
  assertEquals(HttpStatus.SC_OK,response.getStatus());
  response=jsonApi.get(route,securityContextNonAdminUser,null);
  assertEquals(HttpStatus.SC_NOT_FOUND,response.getStatus());
}
