private static Map<Class<? extends Annotation>,Annotation> buildAnnotations(Dimension dimension){
  Map<Class<? extends Annotation>,Annotation> annotations=new HashMap<>();
  annotations.put(ColumnMeta.class,buildColumnMetaAnnotation(dimension));
  annotations.put(DimensionFormula.class,buildDimensionFormulaAnnotation(dimension));
  if (StringUtils.isNotEmpty(dimension.getReadAccess())) {
    annotations.put(ReadPermission.class,buildReadPermissionAnnotation(dimension.getReadAccess()));
  }
  switch(dimension.getType().toUpperCase(Locale.ROOT)){
    case ENUM_ORDINAL:
      annotations.put(Enumerated.class,getEnumeratedAnnotation(EnumType.ORDINAL));
      break;
    case TIME:
      annotations.put(Temporal.class,buildTemporalAnnotation(dimension));
      break;
  }
  return annotations;
}

