  log.info("Starting jetty server on port: {}",elideStandaloneSettings.getPort());
  context.setAttribute(ELIDE_STANDALONE_SETTINGS_ATTR,elideStandaloneSettings);
  setupAsync(context);
  if (elideStandaloneSettings.enableJsonApi()) {
    setupServletHolder(context, ServletContainer.class,elideStandaloneSettings.getJsonApiPathSpec(),"com.yahoo.elide.jsonapi.resources",ElideResourceConfig.class.getCanonicalName());
  }
  if (elideStandaloneSettings.enableGraphQL()) {
    setupServletHolder(context, ServletContainer.class,elideStandaloneSettings.getGraphQLApiPathSpec(),"com.yahoo.elide.graphql",ElideResourceConfig.class.getCanonicalName());
  }
  setupSubscription(context);
  if (elideStandaloneSettings.getAsyncProperties().enableExport()) {
    setupServletHolder(context, ServletContainer.class,elideStandaloneSettings.getAsyncProperties().getExportApiPathSpec(),"com.yahoo.elide.async.resources",ElideResourceConfig.class.getCanonicalName());
  }
  setupMonitoring(context);
  if (elideStandaloneSettings.enableApiDocs()) {
    setupServletHolder(context, ServletContainer.class,elideStandaloneSettings.getApiDocsPathSpec(),"com.yahoo.elide.swagger.resources",ElideResourceConfig.class.getCanonicalName());
  }
  elideStandaloneSettings.updateServletContextHandler(context);
  try (Server jettyServer = new Server(elideStandaloneSettings.getPort())) {
    jettyServer.start();
    log.info("Jetty started!");
    if (block) {
      jettyServer.join();
    }
  } catch (  Exception e) {
    log.error("Unexpected exception caught: {}",e.getMessage(),e);
    throw e;
  }
}

private void setupServletHolder(ServletContextHandler context, Class servletClass, String pathSpec, String providerPackage, Class applicationClass) {
  ServletHolder jerseyServlet=context.addServlet(servletClass, pathSpec);
  jerseyServlet.setInitOrder(0);
  jerseyServlet.setInitParameter("jersey.config.server.provider.packages", providerPackage);
  jerseyServlet.setInitParameter("jakarta.ws.rs.Application", applicationClass.getCanonicalName());
}

