private void setupServletHolder(ServletContextHandler context, String pathSpec, String providerPackages) {
  ServletHolder jerseyServlet=context.addServlet(ServletContainer.class,pathSpec);
  jerseyServlet.setInitOrder(0);
  jerseyServlet.setInitParameter("jersey.config.server.provider.packages",providerPackages);
  jerseyServlet.setInitParameter("jakarta.ws.rs.Application",ElideResourceConfig.class.getCanonicalName());
}

private Integer getThreadPoolSize(ElideStandaloneSettings settings) {
  return settings.getAsyncProperties().getThreadSize() == null ? AsyncExecutorService.DEFAULT_THREAD_POOL_SIZE : settings.getAsyncProperties().getThreadSize();
}

