/** 
 * Run a map/reduce job for estimating Pi.
 * @return the estimated value of Pi
 */
public static BigDecimal estimate(int numMaps,long numPoints,JobConf jobConf) throws IOException {
  jobConf.setJobName(PiEstimator.class.getSimpleName());
  jobConf.setInputFormat(SequenceFileInputFormat.class);
  jobConf.setOutputKeyClass(BooleanWritable.class);
  jobConf.setOutputValueClass(LongWritable.class);
  jobConf.setOutputFormat(SequenceFileOutputFormat.class);
  jobConf.setMapperClass(PiMapper.class);
  jobConf.setNumMapTasks(numMaps);
  jobConf.setReducerClass(PiReducer.class);
  jobConf.setNumReduceTasks(1);
  jobConf.setSpeculativeExecution(false);
  final Path inDir=new Path(TMP_DIR,"in");
  final Path outDir=new Path(TMP_DIR,"out");
  FileInputFormat.setInputPaths(jobConf,inDir);
  FileOutputFormat.setOutputPath(jobConf,outDir);
  final FileSystem fs=FileSystem.get(jobConf);
  if (fs.exists(TMP_DIR)) {
    throw new IOException("Tmp directory " + fs.makeQualified(TMP_DIR) + " already exists.  Please remove it first.");
  }
  if (!fs.mkdirs(inDir)) {
    throw new IOException("Cannot create input directory " + inDir);
  }
  try {
    for (int i=0; i < numMaps; ++i) {
      final Path file=new Path(inDir,"part" + i);
      final LongWritable offset=new LongWritable(i * numPoints);
      final LongWritable size=new LongWritable(numPoints);
      final SequenceFile.Writer writer=SequenceFile.createWriter(fs,jobConf,file,LongWritable.class,LongWritable.class,CompressionType.NONE);
      try {
        writer.append(offset,size);
      }
  finally {
        writer.close();
      }
      System.out.println("Wrote input for Map #" + i);
    }
    System.out.println("Starting Job");
    final long startTime=System.currentTimeMillis();
    JobClient.runJob(jobConf);
    final double duration=(System.currentTimeMillis() - startTime) / 1000.0;
    System.out.println("Job Finished in " + duration + " seconds");
    Path inFile=new Path(outDir,"reduce-out");
    LongWritable numInside=new LongWritable();
    LongWritable numOutside=new LongWritable();
    SequenceFile.Reader reader=new SequenceFile.Reader(fs,inFile,jobConf);
    try {
      reader.next(numInside,numOutside);
    }
  finally {
      reader.close();
    }
    return BigDecimal.valueOf(4).setScale(20).multiply(BigDecimal.valueOf(numInside.get())).divide(BigDecimal.valueOf(numMaps)).divide(BigDecimal.valueOf(numPoints));
  }
  finally {
    fs.delete(TMP_DIR,true);
  }
}
