      if (executor_ != null) {
        executor_.shutdownNow();
      }
    }
  } catch (final Exception e) {
    LOG.error("Exception during closing",e);
  } finally {
    clearResources();
    if (toThrow != null) {
      throw toThrow;
    }
  }
