private String[] getExpectedAlertsForBrowser(Annotation annotation) {
  switch(browserVersion_) {
    case BrowserVersion.INTERNET_EXPLORER:
      return firstDefinedOrGiven(expectedAlerts,annotation.IE(),annotation.DEFAULT());
    case BrowserVersion.EDGE:
      return firstDefinedOrGiven(expectedAlerts,annotation.EDGE(),annotation.DEFAULT());
    case BrowserVersion.FIREFOX_ESR:
      return firstDefinedOrGiven(expectedAlerts,annotation.FF_ESR(),annotation.DEFAULT());
    case BrowserVersion.FIREFOX:
      return firstDefinedOrGiven(expectedAlerts,annotation.FF(),annotation.DEFAULT());
    case BrowserVersion.CHROME:
      return firstDefinedOrGiven(expectedAlerts,annotation.CHROME(),annotation.DEFAULT());
    default:
      return NO_ALERTS_DEFINED;
  }
}

