private String[] getAlertsForBrowser(Annotation annotation, BrowserVersion browserVersion_) {
  String[] expectedAlerts=NO_ALERTS_DEFINED;
  if (annotation != null) {
    if (isDefined(annotation.value())) {
      expectedAlerts=annotation.value();
    } else {
      switch(browserVersion_) {
        case BrowserVersion.INTERNET_EXPLORER:
          expectedAlerts=firstDefined(annotation.IE(),annotation.DEFAULT());
          break;
        case BrowserVersion.EDGE:
          expectedAlerts=firstDefined(annotation.EDGE(),annotation.DEFAULT());
          break;
        case BrowserVersion.FIREFOX_ESR:
          expectedAlerts=firstDefined(annotation.FF_ESR(),annotation.DEFAULT());
          break;
        case BrowserVersion.FIREFOX:
          expectedAlerts=firstDefined(annotation.FF(),annotation.DEFAULT());
          break;
        case BrowserVersion.CHROME:
          expectedAlerts=firstDefined(annotation.CHROME(),annotation.DEFAULT());
          break;
      }
    }
  }
  return expectedAlerts;
}

