private String[] getExpectedAlerts(Object alerts, BrowserVersion browserVersion) {
  String[] expectedAlerts = NO_ALERTS_DEFINED;
  switch (browserVersion) {
    case INTERNET_EXPLORER:
      expectedAlerts = firstDefinedOrGiven(alerts.IE(), alerts.DEFAULT());
      break;
    case EDGE:
      expectedAlerts = firstDefinedOrGiven(alerts.EDGE(), alerts.DEFAULT());
      break;
    case FIREFOX_ESR:
      expectedAlerts = firstDefinedOrGiven(alerts.FF_ESR(), alerts.DEFAULT());
      break;
    case FIREFOX:
      expectedAlerts = firstDefinedOrGiven(alerts.FF(), alerts.DEFAULT());
      break;
    case CHROME:
      expectedAlerts = firstDefinedOrGiven(alerts.CHROME(), alerts.DEFAULT());
      break;
  }
  return expectedAlerts;
}

