private void setAlerts(final WebTestCase testCase,final Method method){
  final Alerts alerts=method.getAnnotation(Alerts.class);
  String[] expectedAlerts={};
  if (alerts != null) {
    expectedAlerts=NO_ALERTS_DEFINED;
    if (isDefined(alerts.value())) {
      expectedAlerts=alerts.value();
    }
    else {
      expectedAlerts=getAlertsForBrowser(alerts);
    }
  }
  if (isRealBrowser()) {
    final BuggyWebDriver buggyWebDriver=method.getAnnotation(BuggyWebDriver.class);
    if (buggyWebDriver != null) {
      if (isDefined(buggyWebDriver.value())) {
        expectedAlerts=buggyWebDriver.value();
      }
      else {
        expectedAlerts=getAlertsForBrowser(buggyWebDriver);
      }
    }
  }
  else {
    final HtmlUnitNYI htmlUnitNYI=method.getAnnotation(HtmlUnitNYI.class);
    if (htmlUnitNYI != null) {
      expectedAlerts=getAlertsForBrowser(htmlUnitNYI);
    }
  }
  testCase.setExpectedAlerts(expectedAlerts);
}

private String[] getAlertsForBrowser(Alerts alerts) {
  switch (browserVersion_) {
    case BrowserVersion.INTERNET_EXPLORER:
      return firstDefined(alerts.IE(),alerts.DEFAULT());
    case BrowserVersion.EDGE:
      return firstDefined(alerts.EDGE(),alerts.DEFAULT());
    case BrowserVersion.FIREFOX_ESR:
      return firstDefined(alerts.FF_ESR(),alerts.DEFAULT());
    case BrowserVersion.FIREFOX:
      return firstDefined(alerts.FF(),alerts.DEFAULT());
    case BrowserVersion.CHROME:
      return firstDefined(alerts.CHROME(),alerts.DEFAULT());
    default:
      return NO_ALERTS_DEFINED;
  }
}

