private String[] getExpectedAlertsForBrowser(final Object alertsOrBuggyWebDriverOrHtmlUnitNYI) {
  if (browserVersion_ == BrowserVersion.INTERNET_EXPLORER) {
    return firstDefinedOrGiven(alertsOrBuggyWebDriverOrHtmlUnitNYI.IE(), alertsOrBuggyWebDriverOrHtmlUnitNYI.DEFAULT());
  }
  else if (browserVersion_ == BrowserVersion.EDGE) {
    return firstDefinedOrGiven(alertsOrBuggyWebDriverOrHtmlUnitNYI.EDGE(), alertsOrBuggyWebDriverOrHtmlUnitNYI.DEFAULT());
  }
  else if (browserVersion_ == BrowserVersion.FIREFOX_ESR) {
    return firstDefinedOrGiven(alertsOrBuggyWebDriverOrHtmlUnitNYI.FF_ESR(), alertsOrBuggyWebDriverOrHtmlUnitNYI.DEFAULT());
  }
  else if (browserVersion_ == BrowserVersion.FIREFOX) {
    return firstDefinedOrGiven(alertsOrBuggyWebDriverOrHtmlUnitNYI.FF(), alertsOrBuggyWebDriverOrHtmlUnitNYI.DEFAULT());
  }
  else if (browserVersion_ == BrowserVersion.CHROME) {
    return firstDefinedOrGiven(alertsOrBuggyWebDriverOrHtmlUnitNYI.CHROME(), alertsOrBuggyWebDriverOrHtmlUnitNYI.DEFAULT());
  }
  return new String[] {};
}

