private void setExpectedAlertsForBrowserVersion(BrowserVersion source, String[] expectedAlerts) {
  switch(browserVersion_) {
    case BrowserVersion.INTERNET_EXPLORER:
      expectedAlerts=firstDefinedOrGiven(expectedAlerts,source.IE(),source.DEFAULT());
      break;
    case BrowserVersion.EDGE:
      expectedAlerts=firstDefinedOrGiven(expectedAlerts,source.EDGE(),source.DEFAULT());
      break;
    case BrowserVersion.FIREFOX_ESR:
      expectedAlerts=firstDefinedOrGiven(expectedAlerts,source.FF_ESR(),source.DEFAULT());
      break;
    case BrowserVersion.FIREFOX:
      expectedAlerts=firstDefinedOrGiven(expectedAlerts,source.FF(),source.DEFAULT());
      break;
    case BrowserVersion.CHROME:
      expectedAlerts=firstDefinedOrGiven(expectedAlerts,source.CHROME(),source.DEFAULT());
      break;
  }
}

