private String[] getAlertsBasedOnBrowserVersion(final String[] expectedAlerts, final Object obj, final BrowserVersion browserVersion_) {
  switch (browserVersion_) {
    case INTERNET_EXPLORER:
      return firstDefinedOrGiven(expectedAlerts, obj.IE(), obj.DEFAULT());
    case EDGE:
      return firstDefinedOrGiven(expectedAlerts, obj.EDGE(), obj.DEFAULT());
    case FIREFOX_ESR:
      return firstDefinedOrGiven(expectedAlerts, obj.FF_ESR(), obj.DEFAULT());
    case FIREFOX:
      return firstDefinedOrGiven(expectedAlerts, obj.FF(), obj.DEFAULT());
    case CHROME:
      return firstDefinedOrGiven(expectedAlerts, obj.CHROME(), obj.DEFAULT());
    default:
      return expectedAlerts;
  }
}

