private void assignAlertsBasedOnBrowserVersionAndAnnotations(final BuggyWebDriver buggyWebDriver, final HtmlUnitNYI htmlUnitNYI) {
  switch (browserVersion_) {
    case BrowserVersion.INTERNET_EXPLORER:
      expectedAlerts=firstDefinedOrGiven(expectedAlerts, buggyWebDriver != null ? buggyWebDriver.IE() : htmlUnitNYI.IE(), buggyWebDriver != null ? buggyWebDriver.DEFAULT() : null);
      break;
    case BrowserVersion.EDGE:
      expectedAlerts=firstDefinedOrGiven(expectedAlerts, buggyWebDriver != null ? buggyWebDriver.EDGE() : htmlUnitNYI.EDGE(), buggyWebDriver != null ? buggyWebDriver.DEFAULT() : null);
      break;
    case BrowserVersion.FIREFOX_ESR:
      expectedAlerts=firstDefinedOrGiven(expectedAlerts, buggyWebDriver != null ? buggyWebDriver.FF_ESR() : htmlUnitNYI.FF_ESR(), buggyWebDriver != null ? buggyWebDriver.DEFAULT() : null);
      break;
    case BrowserVersion.FIREFOX:
      expectedAlerts=firstDefinedOrGiven(expectedAlerts, buggyWebDriver != null ? buggyWebDriver.FF() : htmlUnitNYI.FF(), buggyWebDriver != null ? buggyWebDriver.DEFAULT() : null);
      break;
    case BrowserVersion.CHROME:
      expectedAlerts=firstDefinedOrGiven(expectedAlerts, buggyWebDriver != null ? buggyWebDriver.CHROME() : htmlUnitNYI.CHROME(), buggyWebDriver != null ? buggyWebDriver.DEFAULT() : null);
