public List<A> list(String sql,Object... params){
  if (sql == null || sql.isEmpty()) {
    throw new IllegalArgumentException("The SQL query cannot be null or empty.");
  }
  List<A> beanList=new ArrayList<>();
  grammarCheck(sql,DMLTypes.SELECT);
  int paramSize=getParameterNum(sql,params);
  Connection conn=null;
  PreparedStatement statement=null;
  ResultSet resultSet=null;
  try {
    conn=JdbcPool.getReadConnection();
    statement=conn.prepareStatement(sql);
    if (paramSize > 0) {
      statement=bindParameters(statement,params);
    }
    resultSet=statement.executeQuery();
    String[] selectFields=parseSelectFields(sql);
    while (resultSet.next()) {
      beanList.add(resultSet(selectFields,resultSet));
    }
  }
  catch (Exception e) {
    logger.error("sql error",e);
  }
  finally {
    try {
      if (resultSet != null) resultSet.close();
    } catch (SQLException e) {
      logger.error("Error closing ResultSet", e);
    }
    try {
      if (statement != null) statement.close();
    } catch (SQLException e) {
      logger.error("Error closing Statement", e);
    }
    try {
      if (conn != null) conn.close();
    } catch (SQLException e) {
      logger.error("Error closing Connection", e);
    }
  }
  return beanList;
}

public List<A> list(String sql,Object... params) throws Exception {
  List<A> beanList=new ArrayList<>();
  grammarCheck(sql,DMLTypes.SELECT);
  int paramSize=getParameterNum(sql,params);
  String[] selectFields=parseSelectFields(sql);
  try (Connection conn = JdbcPool.getReadConnection(); PreparedStatement statement = conn.prepareStatement(sql)) {
    statement=bindParameters(statement,params);
    ResultSet resultSet=statement.executeQuery();
    while (resultSet.next()) {
      beanList.add(resultSet(selectFields,resultSet));
    }
  }
  catch (Exception e) {
    logger.error("sql error",e);
    throw e;
  }
  return beanList;
}

