  }
  if (MCCounterHelper.isGroupByCounterEvent(event)) {
    handleGroupByCounterEvent(event);
  }
}

private Long calculateTotal(JetstreamEvent event) {
  for (Map.Entry<String,Object> entry : event.entrySet()) {
    if (entry.getKey().toLowerCase().contains("total")) {
      return (Long)entry.getValue();
    }
  }
  return null;
}

