@Override protected void processApplicationEvent(final ApplicationEvent event){
  if (event instanceof ContextBeanChangedEvent) {
    ContextBeanChangedEvent bcInfo=(ContextBeanChangedEvent)event;
    Optional<SessionizerConfig> newBeanOptional = Optional.ofNullable((SessionizerConfig) (bcInfo.getApplicationContext().getBean(config.getBeanName())));
    newBeanOptional.ifPresent(newBean -> {
      if (newBean != lastConfig) {
        lastConfig=newBean;
        SessionizerConfigValidator validator=new SessionizerConfigValidator(config,newBean);
        List<String> errors=validator.validate();
        if (!errors.isEmpty()) {
          throw new IllegalArgumentException("Config error: " + errors);
        }
        setConfigProperties(newBean);
        boolean isSuccess=false;
        try {
          for (SessionizerRunnable task : tasks) {
            task.newCompiledConfig=new CompiledConfig(config,task);
          }
          isSuccess=true;
        } finally {
          if (!isSuccess) {
            resetConfigProperties();
          }
        }
        refreshConfig();
      }
    });
