default List<ApiReqParam> handle(JavaMethod method, ProjectDocConfigBuilder projectBuilder) {

Map<String,String> constantsMap = projectBuilder.getConstantsMap();
List<ApiReqParam> mappingHeaders = new ArrayList<>();
List<JavaAnnotation> annotations = method.getAnnotations();
HeaderAnnotation headerAnnotation = getHeaderAnnotation();

annotations.stream()
  .filter(annotation -> isMapping(annotation.getType().getValue()) && annotation.getNamedParameter('headers') != null)
  .forEach(annotation -> {
    Object headersObject = annotation.getNamedParameter('headers');
    if (!headersObject.toString().startsWith('[')) {
      processMappingHeaders(StringUtil.removeQuotes(headersObject.toString()), mappingHeaders);
    } else {
      List<String> headers = (LinkedList)headersObject;
      headers.stream()
        .filter(header -> !header.startsWith('!'))
        .forEach(header -> processMappingHeaders(StringUtil.removeQuotes(header), mappingHeaders));
    }
  });

List<ApiReqParam> reqHeaders = new ArrayList<>();

method.getParameters().stream()
  .filter(javaParameter -> javaParameter.getAnnotations().stream().anyMatch(annotation -> headerAnnotation.getAnnotationName().equals(annotation.getType().getValue())))
  .forEach(javaParameter -> { ... });

return Stream.of(mappingHeaders,reqHeaders).flatMap(Collection::stream).distinct().collect(Collectors.toList());
}

