/** 
 * build request params
 * @param docJavaMethod docJavaMethod
 * @param builder       builder
 * @return ApiMethodReqParam
 */
private ApiMethodReqParam requestParams(final DocJavaMethod docJavaMethod,ProjectDocConfigBuilder builder){
  List<ApiParam> paramList=new ArrayList<>();
  List<DocJavaParameter> parameterList=getJavaParameterList(builder,docJavaMethod,null);
  if (parameterList.isEmpty()) {
    return ApiMethodReqParam.builder().setPathParams(new ArrayList<>(0)).setQueryParams(paramList).setRequestParams(new ArrayList<>(0));
  }
  boolean isStrict=builder.getApiConfig().isStrict();
  JavaMethod javaMethod=docJavaMethod.getJavaMethod();
  String className=javaMethod.getDeclaringClass().getCanonicalName();
  Map<String,String> paramTagMap=docJavaMethod.getParamTagMap();
  Map<String,String> paramsComments=docJavaMethod.getParamsComments();
  Map<String,String> constantsMap=builder.getConstantsMap();
  boolean requestFieldToUnderline=builder.getApiConfig().isRequestFieldToUnderline();
  Set<String> ignoreSets=ignoreParamsSets(javaMethod);
  out:   for (  DocJavaParameter apiParameter : parameterList) {
    JavaParameter parameter=apiParameter.getJavaParameter();
    String paramName=parameter.getName();
    if (ignoreSets.contains(paramName)) {
      continue;
    }
    String typeName=apiParameter.getGenericCanonicalName();
    String simpleName=apiParameter.getTypeValue().toLowerCase();
    String fullTypeName=apiParameter.getFullyQualifiedName();
    String simpleTypeName=apiParameter.getTypeValue();
    if (!paramTagMap.containsKey(paramName) && JavaClassValidateUtil.isPrimitive(fullTypeName) && isStrict) {
      throw new RuntimeException("ERROR: Unable to find javadoc @QueryParam for actual param \"" + paramName + "\" in method "+ javaMethod.getName()+ " from "+ className);
    }
    if (requestFieldToUnderline) {
      paramName=StringUtil.camelToUnderline(paramName);
    }
    String mockValue=JavaFieldUtil.createMockValue(paramsComments,paramName,typeName,simpleTypeName);
    JavaClass javaClass=builder.getJavaProjectBuilder().getClassByName(fullTypeName);
    List<JavaAnnotation> annotations=parameter.getAnnotations();
    Set<String> groupClasses=JavaClassUtil.getParamGroupJavaClass(annotations,builder.getJavaProjectBuilder());
    StringBuilder comment=new StringBuilder(this.paramCommentResolve(paramTagMap.get(paramName)));
    boolean isPathVariable=false;
    boolean isRequestBody=false;
    String strRequired="false";
    if (CollectionUtil.isNotEmpty(annotations)) {
      for (      JavaAnnotation annotation : annotations) {
        String annotationName=annotation.getType().getFullyQualifiedName();
        if (JakartaJaxrsAnnotations.JAX_HEADER_PARAM_FULLY.equals(annotationName) || JAXRSAnnotations.JAX_HEADER_PARAM_FULLY.equals(annotationName)) {
          continue out;
        }
        if (JakartaJaxrsAnnotations.JAX_DEFAULT_VALUE_FULLY.equals(annotationName) || JAXRSAnnotations.JAX_DEFAULT_VALUE_FULLY.equals(annotationName)) {
          mockValue=StringUtil.removeQuotes(DocUtil.getRequestHeaderValue(annotation));
          mockValue=DocUtil.handleConstants(constantsMap,mockValue);
        }
        if (JakartaJaxrsAnnotations.JAX_PATH_PARAM_FULLY.equals(annotationName) || JakartaJaxrsAnnotations.JAXB_REST_PATH_FULLY.equals(annotationName) || JAXRSAnnotations.JAX_PATH_PARAM_FULLY.equals(annotationName)) {
          isPathVariable=true;
          strRequired="true";
        }
        if (JavaClassValidateUtil.isJSR303Required(annotation.getType().getValue())) {
          strRequired="true";
        }
      }
      comment.append(JavaFieldUtil.getJsrComment(annotations));
    }
 else {
      isRequestBody=true;
    }
    boolean required=Boolean.parseBoolean(strRequired);
    boolean queryParam=!isRequestBody && !isPathVariable;
    if (JavaClassValidateUtil.isCollection(fullTypeName) || JavaClassValidateUtil.isArray(fullTypeName)) {
      String[] gicNameArr=DocClassUtil.getSimpleGicName(typeName);
      String gicName=gicNameArr[0];
      if (JavaClassValidateUtil.isArray(gicName)) {
        gicName=gicName.substring(0,gicName.indexOf("["));
      }
      JavaClass gicJavaClass=builder.getJavaProjectBuilder().getClassByName(gicName);
      if (gicJavaClass.isEnum()) {
        Object value=JavaClassUtil.getEnumValue(gicJavaClass,Boolean.TRUE);
        ApiParam param=ApiParam.of().setField(paramName).setDesc(comment + ",[array of enum]").setRequired(required).setPathParam(isPathVariable).setQueryParam(queryParam).setId(paramList.size() + 1).setType("array").setValue(String.valueOf(value));
        paramList.add(param);
      }
 else       if (JavaClassValidateUtil.isPrimitive(gicName)) {
        String shortSimple=DocClassUtil.processTypeNameForParams(gicName);
        ApiParam param=ApiParam.of().setField(paramName).setDesc(comment + ",[array of " + shortSimple+ "]").setRequired(required).setPathParam(isPathVariable).setQueryParam(queryParam).setId(paramList.size() + 1).setType("array").setValue(DocUtil.getValByTypeAndFieldName(gicName,paramName));
        paramList.add(param);
      }
 else {
        int id=paramList.size() + 1;
        ApiParam param=ApiParam.of().setField(paramName).setDesc(comment + ",[array of object]").setRequired(required).setPathParam(isPathVariable).setQueryParam(queryParam).setId(id).setType("array");
        paramList.add(param);
        List<ApiParam> apiParamList=ParamsBuildHelper.buildParams(typeName,"└─",1,"true",Boolean.FALSE,new HashMap<>(),builder,groupClasses,id,Boolean.FALSE,null);
        paramList.addAll(apiParamList);
      }
    }
 else     if (JavaClassValidateUtil.isPrimitive(fullTypeName)) {
      ApiParam param=ApiParam.of().setField(paramName).setType(DocClassUtil.processTypeNameForParams(simpleName)).setId(paramList.size() + 1).setPathParam(isPathVariable).setQueryParam(queryParam).setValue(mockValue).setDesc(comment.toString()).setRequired(required).setVersion(DocGlobalConstants.DEFAULT_VERSION);
      paramList.add(param);
    }
 else     if (JavaClassValidateUtil.isMap(fullTypeName)) {
      log.warning("When using smart-doc, it is not recommended to use Map to receive parameters, Check it in " + javaMethod.getDeclaringClass().getCanonicalName() + "#"+ javaMethod.getName());
      if (JavaClassValidateUtil.isMap(typeName)) {
        ApiParam apiParam=ApiParam.of().setField(paramName).setType("map").setId(paramList.size() + 1).setPathParam(isPathVariable).setQueryParam(queryParam).setDesc(comment.toString()).setRequired(required).setVersion(DocGlobalConstants.DEFAULT_VERSION);
        paramList.add(apiParam);
        continue;
      }
      String[] gicNameArr=DocClassUtil.getSimpleGicName(typeName);
      if (JavaClassValidateUtil.isPrimitive(gicNameArr[1])) {
        ApiParam apiParam=ApiParam.of().setField(paramName).setType("map").setId(paramList.size() + 1).setPathParam(isPathVariable).setQueryParam(queryParam).setDesc(comment.toString()).setRequired(required).setVersion(DocGlobalConstants.DEFAULT_VERSION);
        paramList.add(apiParam);
      }
 else {
        paramList.addAll(ParamsBuildHelper.buildParams(gicNameArr[1],DocGlobalConstants.EMPTY,0,"true",Boolean.FALSE,new HashMap<>(),builder,groupClasses,0,Boolean.FALSE,null));
      }
    }
 else     if (JavaClassValidateUtil.isFile(typeName)) {
      ApiParam param=ApiParam.of().setField(paramName).setType("file").setId(paramList.size() + 1).setQueryParam(true).setRequired(required).setVersion(DocGlobalConstants.DEFAULT_VERSION).setDesc(comment.toString());
      if (typeName.contains("[]") || typeName.endsWith(">")) {
        comment.append("(array of file)");
        param.setDesc(comment.toString());
        param.setHasItems(true);
      }
      paramList.add(param);
    }
 else     if (javaClass.isEnum()) {
      String o=JavaClassUtil.getEnumParams(javaClass);
      Object value=JavaClassUtil.getEnumValue(javaClass,true);
      ApiParam param=ApiParam.of().setField(paramName).setId(paramList.size() + 1).setPathParam(isPathVariable).setQueryParam(queryParam).setValue(String.valueOf(value)).setType("enum").setDesc(StringUtil.removeQuotes(o)).setRequired(required).setVersion(DocGlobalConstants.DEFAULT_VERSION).setEnumValues(JavaClassUtil.getEnumValues(javaClass));
      paramList.add(param);
    }
 else {
      paramList.addAll(ParamsBuildHelper.buildParams(typeName,DocGlobalConstants.EMPTY,0,"true",Boolean.FALSE,new HashMap<>(),builder,groupClasses,0,Boolean.FALSE,null));
    }
  }
  List<ApiParam> pathParams=new ArrayList<>();
  List<ApiParam> queryParams=new ArrayList<>();
  List<ApiParam> bodyParams=new ArrayList<>();
  for (  ApiParam param : paramList) {
    param.setValue(StringUtil.removeDoubleQuotes(param.getValue()));
    if (param.isPathParam()) {
      param.setId(pathParams.size() + 1);
      pathParams.add(param);
    }
 else     if (param.isQueryParam()) {
      param.setId(queryParams.size() + 1);
      queryParams.add(param);
    }
 else {
      param.setId(bodyParams.size() + 1);
      bodyParams.add(param);
    }
  }
  return ApiMethodReqParam.builder().setRequestParams(bodyParams).setPathParams(pathParams).setQueryParams(queryParams);
}
