      methodApi.setPathParams(buildParams(apiMethodDoc.getPathParams()));
    }
    if (CollectionUtil.isNotEmpty(apiMethodDoc.getQueryParams()) && DocGlobalConstants.FILE_CONTENT_TYPE.equals(apiMethodDoc.getContentType())) {
