public void buildSearchJs(ApiConfig config,JavaProjectBuilder javaProjectBuilder,List<ApiDoc> apiDocList,String template){
  List<ApiErrorCode> errorCodeList=DocUtil.errorCodeDictToList(config,javaProjectBuilder);
  Template tpl=BeetlTemplateUtil.getByName(template);
  List<ApiDoc> apiDocs=new ArrayList<>();
  for (ApiDoc apiDoc1 : apiDocList) {
    apiDoc1.setOrder(apiDocs.size() + 1);
    apiDocs.add(apiDoc1);
  }
  boolean isOnlyDefaultGroup=apiDocList.size() == 1;
  Map<String,String> titleMap=setDirectoryLanguageVariable(config,tpl);
  addApiDocIfNotEmpty(errorCodeList, titleMap.get(TemplateVariable.ERROR_LIST_TITLE.getVariable()), apiDocs, apiDocList, isOnlyDefaultGroup, "error_code_list", "error");
  List<ApiDocDict> apiDocDictList=DocUtil.buildDictionary(config,javaProjectBuilder);
  addApiDocIfNotEmpty(apiDocDictList, titleMap.get(TemplateVariable.DICT_LIST_TITLE.getVariable()), apiDocs, apiDocList, isOnlyDefaultGroup, "dict_list", "dict");
  tpl.binding(TemplateVariable.API_DOC_LIST.getVariable(),apiDocs);
  try (BufferedWriter writer = Files.newBufferedWriter(Paths.get(config.getOutPath(), DocGlobalConstants.FILE_SEPARATOR, DocGlobalConstants.SEARCH_JS_OUT))) {
    writer.write(tpl.render());
  } catch (IOException e) {
    e.printStackTrace();
  }
}

private void addApiDocIfNotEmpty(Collection<?> collection, String title, List<ApiDoc> apiDocs, List<ApiDoc> apiDocList, boolean isOnlyDefaultGroup, String link, String alias) {
  if (CollectionUtil.isNotEmpty(collection)) {
    ApiDoc apiDoc1=new ApiDoc();
    int codeIndex=0;
    if (isOnlyDefaultGroup) {
      codeIndex=apiDocs.get(0).getChildrenApiDocs().size();
    }
 else {
      codeIndex=apiDocList.size();
    }
    apiDoc1.setOrder(codeIndex + 1);
    apiDoc1.setDesc(title);
    apiDoc1.setList(new ArrayList<>(0));
    apiDoc1.setLink(link);
    apiDoc1.setAlias(alias);
    apiDoc1.setGroup(apiDoc1.getDesc());
    if (isOnlyDefaultGroup) {
      apiDocs.get(0).getChildrenApiDocs().add(apiDoc1);
    }
 else {
      apiDocs.add(apiDoc1);
    }
  }
}

