    int codeIndex = isOnlyDefaultGroup ? apiDocs.get(0).getChildrenApiDocs().size() : apiDocList.size();
    apiDoc.setOrder(codeIndex + 1);
    apiDoc.setDesc(desc);
    apiDoc.setList(new ArrayList<>(0));
    apiDoc.setLink(link);
    apiDoc.setAlias(alias);
    apiDoc.setGroup(apiDoc.getDesc());
    return apiDoc;
