    Map<String, String> constantsMap = projectBuilder.getConstantsMap();
    List<ApiReqParam> mappingHeaders = new ArrayList<>();
    List<ApiReqParam> reqHeaders = new ArrayList<>();

    String HEADER_ANNOTATION = "headers";
    String EXCLAMATION_MARK = "!";
    HeaderAnnotation headerAnnotation = getHeaderAnnotation();

    method.getAnnotations().stream()
        .filter(annotation -> isMapping(annotation.getType().getValue()) && annotation.getNamedParameter(HEADER_ANNOTATION) != null)
        .forEach(annotation -> processHeaderAnnotation(annotation, mappingHeaders, HEADER_ANNOTATION, EXCLAMATION_MARK));

    method.getParameters().stream()
        .forEach(javaParameter -> processJavaParameter(javaParameter, method, constantsMap, reqHeaders, headerAnnotation));

    return Stream.concat(mappingHeaders.stream(), reqHeaders.stream())
        .distinct()
        .collect(Collectors.toList());
}

