List<ApiDoc> handleTagsApiDoc(List<ApiDoc> apiDocList) {
  if (isEmpty(apiDocList)) {
    return Collections.emptyList();
  }

  Map<String, ApiDoc> copyMap = new HashMap<>();

  apiDocList.forEach(doc -> {
    String[] tags = doc.getTags();
    if (isEmpty(tags)) {
      tags = new String[] {doc.getPackageName() + '.' + doc.getName()};
    }

    for (String tag : tags) {
      String trimmedTag = trim(tag);

      copyMap.computeIfPresent(trimmedTag, (k, v) -> {
        List<ApiMethodDoc> list = isEmpty(v.getList()) ? new ArrayList<>() : v.getList();
        list.addAll(doc.getList());
        v.setList(list);
        return v;
      });

      copyMap.putIfAbsent(trimmedTag, doc);
    }
  });

  copyMap.forEach((k, v) -> {
    List<ApiMethodDoc> methodDocList = v.getList();

    methodDocList.forEach(method -> {
      String[] tags = method.getTags();
      if (isEmpty(tags)) {
        return;
      }

      for (String tag : tags) {
        String trimmedTag = trim(tag);

        copyMap.computeIfPresent(trimmedTag, (k1, v2) -> {
          method.setOrder(v2.getList().size() + 1);
          v2.getList().add(method);
          return v2;
        });

        copyMap.putIfAbsent(trimmedTag, ApiDoc.buildTagApiDoc(v, trimmedTag, method));
      }
    });
  });

  List<ApiDoc> apiDocs = new ArrayList<>(copyMap.values());
  int index = apiDocs.size() - 1;

  for (ApiDoc apiDoc : apiDocs) {
    if (apiDoc.getOrder() == null) {
