public RequestMapping handle(ProjectDocConfigBuilder projectBuilder,String controllerBaseUrl,JavaMethod method,FrameworkAnnotations frameworkAnnotations,RequestMappingFunc requestMappingFunc){
if (Objects.nonNull(method.getTagByName(IGNORE))) {
return null;
}
List<JavaAnnotation> annotations=getAnnotations(method);
String methodType="GET";
String shortUrl=null;
String mediaType=null;
boolean deprecated=false;
for (JavaAnnotation annotation : annotations) {
String annotationName=annotation.getType().getName();
if (DocAnnotationConstants.DEPRECATED.equals(annotationName)) {
deprecated=true;
}
if (SolonAnnotations.REQUEST_MAPPING.equals(annotationName) || SolonAnnotations.REQUEST_MAPPING_FULLY.equals(annotationName)) {
shortUrl=DocUtil.handleMappingValue(annotation);
Object produces=annotation.getNamedParameter("produces");
if (Objects.nonNull(produces)) {
mediaType=produces.toString();
}
}
switch(annotationName) {
case SolonAnnotations.GET_MAPPING:
case SolonAnnotations.GET_MAPPING_FULLY:
methodType=HTTP_METHOD_GET;
break;
case SolonAnnotations.POST_MAPPING:
case SolonAnnotations.POST_MAPPING_FULLY:
methodType=HTTP_METHOD_POST;
break;
case SolonAnnotations.PUT_MAPPING:
case SolonAnnotations.PUT_MAPPING_FULLY:
methodType=HTTP_METHOD_PUT;
break;
case SolonAnnotations.PATCH_MAPPING:
case SolonAnnotations.PATCH_MAPPING_FULLY:
methodType=HTTP_METHOD_PATCH;
break;
case SolonAnnotations.DELETE_MAPPING:
case SolonAnnotations.DELETE_MAPPING_FULLY:
methodType=HTTP_METHOD_DELETE;
break;
}
}
RequestMapping requestMapping=RequestMapping.builder().setMediaType(mediaType).setMethodType(methodType).setDeprecated(deprecated).setShortUrl(shortUrl);
requestMapping=formatMappingData(projectBuilder,controllerBaseUrl,requestMapping);
requestMappingFunc.process(method.getDeclaringClass(),requestMapping);
return requestMapping;
}

