private void handleAnnotations(List<JavaAnnotation> annotations) {
  for (JavaAnnotation annotation : annotations) {
    String annotationName = annotation.getType().getName();
    switch (annotationName) {
      case DocAnnotationConstants.DEPRECATED:
        deprecated = true;
        break;
      case SolonAnnotations.REQUEST_MAPPING:
      case SolonAnnotations.REQUEST_MAPPING_FULLY:
        shortUrl = DocUtil.handleMappingValue(annotation);
        Object produces = annotation.getNamedParameter("produces");
        if (Objects.nonNull(produces)) {
          mediaType = produces.toString();
        }
        break;
      case SolonAnnotations.GET_MAPPING:
      case SolonAnnotations.GET_MAPPING_FULLY:
        methodType = Methods.GET.getValue();
        break;
      case SolonAnnotations.POST_MAPPING:
      case SolonAnnotations.POST_MAPPING_FULLY:
        methodType = Methods.POST.getValue();
        break;
      case SolonAnnotations.PUT_MAPPING:
      case SolonAnnotations.PUT_MAPPING_FULLY:
        methodType = Methods.PUT.getValue();
        break;
      case SolonAnnotations.PATCH_MAPPING:
      case SolonAnnotations.PATCH_MAPPING_FULLY:
        methodType = Methods.PATCH.getValue();
        break;
      case SolonAnnotations.DELETE_MAPPING:
      case SolonAnnotations.DELETE_MAPPING_FULLY:
        methodType = Methods.DELETE.getValue();
        break;
      default:
        break;
    }
  }
}

