private static String getType(String typeName){
  if (typeName == null) {
      return "";
  }
  String gicType;
  Pattern pattern = Pattern.compile("([^<\[.]+)(?:<|\[|.|$)");
  Matcher matcher = pattern.matcher(typeName);
  if (matcher.find()) {
      gicType = matcher.group(1).toLowerCase();
  }
  else {
      gicType = "";
  }
  return gicType;
}

private static String getType(String typeName) {
    String gicType;
    Matcher matcher = Pattern.compile("([^.\[<]*)(?:\.([^.<\[]*))?(?:\<([^>]*)\>)?(?:\[(\d*)\])?").matcher(typeName);
    if (matcher.find()) {
        gicType = matcher.group(2);
        if (gicType == null) {
            gicType = matcher.group(1);
        }
    }
    return gicType.toLowerCase();
}

private static String getType(String typeName) {
  String gicType;
  gicType = typeName.split("<")[0];
  gicType = gicType.replace("[", "");
  return gicType.substring(gicType.lastIndexOf(".") + 1).toLowerCase();
}

private static String getType(String typeName){
  String processedType;
  try {
    if (typeName.contains("<")) {
      processedType=typeName.substring(typeName.indexOf("<") + 1,typeName.lastIndexOf(">"));
    }
    else {
      processedType=typeName;
    }
    if (processedType.contains("[")) {
      processedType=processedType.substring(0,processedType.indexOf("["));
    }
    return processedType.substring(processedType.lastIndexOf(".") + 1).toLowerCase();
  } catch (StringIndexOutOfBoundsException e) {
    return "";
  }
}

