  String gicType = typeName.replaceAll("^.*<|>.*$", "").replaceAll("\[.*", "");
  return gicType.substring(gicType.lastIndexOf(".") + 1).toLowerCase();
}

private static String getType(String typeName){
  final char LT = '<';
  final char GT = '>';
  final char BRACKET = '[';
  final char DOT = '.';
  final int ONE = 1;

  if (typeName == null) {
    throw new IllegalArgumentException("typeName cannot be null");
  }

  String gicType;
  if (typeName.contains(String.valueOf(LT))) {
    gicType=typeName.substring(typeName.indexOf(LT) + ONE, typeName.lastIndexOf(GT));
  } else if (gicType.contains(String.valueOf(BRACKET))) {
    gicType=gicType.substring(0, gicType.indexOf(BRACKET));
  } else {
    gicType=typeName;
  }

  return gicType.substring(gicType.lastIndexOf(DOT) + ONE).toLowerCase();
}

