@Nullable private MappingInfo getMappingInfo(Method method,Object handler,Class<?> handlerType){
  Set<Annotation> annotations=AnnotatedElementUtils.findAllMergedAnnotations(method,new LinkedHashSet<>(Arrays.asList(BatchMapping.class,SchemaMapping.class)));
  if (annotations.isEmpty()) {
    return null;
  }
  if (annotations.size() != 1) {
    throw new IllegalArgumentException("Expected either @BatchMapping or @SchemaMapping, not both: " + method.toGenericString());
  }
  String typeName;
  String field;
  boolean batchMapping=false;
  int batchSize=-1;
  HandlerMethod handlerMethod=createHandlerMethod(method,handler,handlerType);
  Annotation annotation=annotations.iterator().next();
  typeName = getTypeName(annotation);
  field = getField(annotation, method);
  if (annotation instanceof BatchMapping) {
    batchMapping=true;
    batchSize=((BatchMapping)annotation).maxBatchSize();
  }
  if (!StringUtils.hasText(typeName)) {
    Optional<SchemaMapping> optionalMapping = Optional.ofNullable(AnnotatedElementUtils.findMergedAnnotation(handlerType,SchemaMapping.class));
    typeName = optionalMapping.map(SchemaMapping::typeName).orElse(typeName);
  }
  if (!StringUtils.hasText(typeName)) {
    for (MethodParameter parameter : handlerMethod.getMethodParameters()) {
      if (!batchMapping) {
        Assert.state(this.argumentResolvers != null,"`argumentResolvers` is not initialized");
        HandlerMethodArgumentResolver resolver=this.argumentResolvers.getArgumentResolver(parameter);
        if (resolver instanceof SourceMethodArgumentResolver) {
          typeName=parameter.getParameterType().getSimpleName();
          break;
        }
      } else {
        if (Collection.class.isAssignableFrom(parameter.getParameterType())) {
          typeName=parameter.nested().getNestedParameterType().getSimpleName();
          break;
        }
      }
    }
  }
  Assert.hasText(typeName,"No parentType specified, and a source/parent method argument was also not found: " + handlerMethod.getShortLogMessage());
  return new MappingInfo(typeName,field,batchMapping,batchSize,handlerMethod);
}

private String getTypeName(Annotation annotation) {
  if (annotation instanceof BatchMapping) {
    return ((BatchMapping) annotation).typeName();
  } else {
    return ((SchemaMapping) annotation).typeName();
  }
}

private String getField(Annotation annotation, Method method) {
  String field = ((Mapping) annotation).field();
  return StringUtils.hasText(field) ? field : method.getName();
}

