private static <T extends QueryByExampleExecutor<?>> void registerExecutors(List<T> executors, Map<String, DataFetcherFactory> factories, CursorStrategy<ScrollPosition> cursorStrategy, ScrollSubrange defaultScrollSubrange) {
  for (T executor : executors) {
    String typeName=RepositoryUtils.getGraphQlTypeName(executor);
    if (typeName != null) {
      Builder<?,?> builder=customize(executor,builder(executor).cursorStrategy(cursorStrategy).defaultScrollSubrange(defaultScrollSubrange));
      factories.put(typeName, new DataFetcherFactory(
        builder::single,
        builder::many,
        builder::scrollable
      ));
    }
  }
}

