private static <T> Factory createDataFetcherFactory(T executor, @Nullable CursorStrategy<ScrollPosition> cursorStrategy, @Nullable ScrollSubrange defaultScrollSubrange) {
  String typeName=RepositoryUtils.getGraphQlTypeName(executor);
  if (typeName == null) {
    return null;
  }
  Builder<?,?> builder=customize(executor,builder(executor).cursorStrategy(cursorStrategy).defaultScrollSubrange(defaultScrollSubrange));
  return new Factory(typeName, new DataFetcherFactory(){
    @Override public DataFetcher<?> single(){
      return builder.single();
    }
    @Override public DataFetcher<?> many(){
      return builder.many();
    }
    @Override public DataFetcher<?> scrollable(){
      return builder.scrollable();
    }
  });
}

private static class Factory {
  String typeName;
  DataFetcherFactory dataFetcherFactory;

  Factory(String typeName, DataFetcherFactory dataFetcherFactory) {
    this.typeName = typeName;
    this.dataFetcherFactory = dataFetcherFactory;
  }
}

