  processExecutors(executors, factories, cursorStrategy, defaultScrollSubrange);
  processExecutors(reactiveExecutors, factories, cursorStrategy, defaultScrollSubrange);
  if (logger.isTraceEnabled()) {
    logger.trace('Auto-registration candidate typeNames ' + factories.keySet());
  }
  return new AutoRegistrationRuntimeWiringConfigurer(factories);
}

private static <T> void processExecutors(List<T> executors, Map<String, DataFetcherFactory> factories, CursorStrategy<ScrollPosition> cursorStrategy, ScrollSubrange defaultScrollSubrange) {
  for (T executor : executors) {
    String typeName=RepositoryUtils.getGraphQlTypeName(executor);
    if (typeName != null) {
      Builder builder=customize(executor,QuerydslDataFetcher.builder(executor).cursorStrategy(cursorStrategy).defaultScrollSubrange(defaultScrollSubrange).customizer(customizer(executor)));
      factories.put(typeName, createDataFetcherFactory(builder));
    }
  }
}

private static DataFetcherFactory createDataFetcherFactory(Builder builder) {
  return new DataFetcherFactory(){
    @Override public DataFetcher<?> single(){
      return builder.single();
    }
    @Override public DataFetcher<?> many(){
      return builder.many();
    }
    @Override public DataFetcher<?> scrollable(){
      return builder.scrollable();
    }
  };
}

