public static RuntimeWiringConfigurer autoRegistrationConfigurer(List<QuerydslPredicateExecutor<?>> executors,List<ReactiveQuerydslPredicateExecutor<?>> reactiveExecutors,@Nullable CursorStrategy<ScrollPosition> cursorStrategy,@Nullable ScrollSubrange defaultScrollSubrange){
  Map<String,DataFetcherFactory> factories=new HashMap<>();
  populateFactories(executors, cursorStrategy, defaultScrollSubrange, factories);
  populateFactories(reactiveExecutors, cursorStrategy, defaultScrollSubrange, factories);
  if (logger.isTraceEnabled()) {
    logger.trace("Auto-registration candidate typeNames " + factories.keySet());
  }
  return new AutoRegistrationRuntimeWiringConfigurer(factories);
}

private static <T extends QuerydslPredicateExecutor<?>> void populateFactories(List<T> executors, CursorStrategy<ScrollPosition> cursorStrategy, ScrollSubrange defaultScrollSubrange, Map<String, DataFetcherFactory> factories) {
  for (T executor : executors) {
    String typeName = RepositoryUtils.getGraphQlTypeName(executor);
    if (typeName != null) {
      Builder builder = customize(executor,QuerydslDataFetcher.builder(executor).cursorStrategy(cursorStrategy).defaultScrollSubrange(defaultScrollSubrange).customizer(customizer(executor)));
      factories.put(typeName, new DataFetcherFactory() {
        public DataFetcher<?> single() { return () -> builder.single(); }
        public DataFetcher<?> many() { return () -> builder.many(); }
        public DataFetcher<?> scrollable() { return () -> builder.scrollable(); }
      });
    }
  }
}

