/** 
 * Use this method to resolve the arguments asynchronously. This is only useful when at least one of the values is a  {@link Mono}
 */
@SuppressWarnings("unchecked") protected Mono<Object[]> toArgsMono(Object[] args){
  List<Mono<Object>> monoList=new ArrayList<>();
  for (  Object arg : args) {
    Mono<Object> argMono=(arg instanceof Mono ? (Mono<Object>)arg : Mono.justOrEmpty(arg));
    monoList.add(argMono.defaultIfEmpty(NO_VALUE));
  }
  return Mono.zip(monoList,values -> {
    for (int i=0; i < values.length; i++) {
      if (values[i] == NO_VALUE) {
        values[i]=null;
      }
    }
    return values;
  }
);
}
