Method_Improvements.Final code:
@Override public String createAuthRequest(String policyName,String jsonKeyValuePairs){
  Map<String,Object> templateData=generateFreemakerTemplateData(jsonKeyValuePairs);
  StringBuilder sb = new StringBuilder(policyName).append(jsonKeyValuePairs).append(getTemplateDataAsAString(templateData));
  String key = Base64.getEncoder().encodeToString(sb.toString().trim().getBytes());
  String cachedRequest=this.requestBuilderCache.get(key);
  if (cachedRequest != null) {
    return cachedRequest;
  }
  String xacmlRequest;
  try (StringWriter out=new StringWriter()){
    Configuration cfg=new Configuration(new Version("2.3.23"));
    cfg.setClassForTemplateLoading(this.getClass(),"/");
    cfg.setDefaultEncoding("UTF-8");
  } catch (IOException e) {
    throw new AttributeEvaluatorException("Failed to initialize StringWriter or Configuration.", e);
  }
  try {
    Template template=cfg.getTemplate(ATTRIBUTE_CONFIG_FILE_NAME);
    template.process(templateData,out);
  } catch (TemplateException e) {
    throw new AttributeEvaluatorException("Failed to process template.", e);
  }
  try {
    xacmlRequest=new JSONObject(out.toString()).get(policyName).toString();
    out.flush();
  } catch (IOException e) {
    throw new AttributeEvaluatorException("Failed to flush StringWriter.", e);
  }
  if (xacmlRequest == null || xacmlRequest.isEmpty()) {
    throw new AttributeEvaluatorException("Generated XACML request is empty or NULL for policy with name : " + policyName);
  }
  return this.requestBuilderCache.putIfAbsent(key,xacmlRequest);
}

