    KEY_STORE_PASSWORD=properties.getProperty("xacml.pdp.keyStore.password");
  }
 catch (  IOException e) {
    logger.error("Failed to read properties from application.properties",e);
    stopApplication();
  }
  if (XACML_PDP_ENTITLEMENT_SERVICE_URL == null) {
