  try {
    this.httpClient=HttpClients.custom().setSSLContext(SSLContextBuilder.create().loadKeyMaterial(loadPfx("classpath:".concat(KEY_STORE),KEY_STORE_PASSWORD.toCharArray()),KEY_STORE_PASSWORD.toCharArray()).loadTrustMaterial(ResourceUtils.getFile("classpath:".concat(TRUST_STORE)),TRUST_STORE_PASSWORD.toCharArray()).build()).build();
  }
 catch (  Exception e) {
