public static DocumentBuilderFactory newInstance(){
  DocumentBuilderFactory factory=DocumentBuilderFactory.newInstance();
  setAttributeSafe(factory, XMLConstants.ACCESS_EXTERNAL_DTD, "");
  setAttributeSafe(factory, XMLConstants.ACCESS_EXTERNAL_SCHEMA, "");
  setFeatureSafe(factory, 'http://apache.org/xml/features/disallow-doctype-decl', true);
  setFeatureSafe(factory, 'http://xml.org/sax/features/external-general-entities', false);
  setFeatureSafe(factory, 'http://xml.org/sax/features/external-parameter-entities', false);
  setFeatureSafe(factory, 'http://apache.org/xml/features/nonvalidating/load-external-dtd', false);
  try {
    factory.setXIncludeAware(false);
    factory.setExpandEntityReferences(false);
  }
 catch (  Exception e) {
    if (log.isWarnEnabled()) {
      log.warn('Caught ' + e.getMessage() + ' attempting to configure your XML parser.');
    }
  }
  return factory;
}

private static void setAttributeSafe(DocumentBuilderFactory factory, String attr, String value) {
  try {
    factory.setAttribute(attr, value);
  } catch (IllegalArgumentException e) {
    if (log.isWarnEnabled()) {
      log.warn(attr + ' property not supported by ' + factory.getClass().getCanonicalName());
    }
  }
}

private static void setFeatureSafe(DocumentBuilderFactory factory, String feature, boolean value) {
  try {
    factory.setFeature(feature, value);
  } catch (ParserConfigurationException e) {
    if (log.isWarnEnabled()) {
      log.warn('FEATURE ' + feature + ' is probably not supported by ' + factory.getClass().getCanonicalName());
    }
  }
}

