public static DocumentBuilderFactory newInstance(){
  DocumentBuilderFactory factory=DocumentBuilderFactory.newInstance();
  setAttributeOrWarn(factory, XMLConstants.ACCESS_EXTERNAL_DTD, "");
  setAttributeOrWarn(factory, XMLConstants.ACCESS_EXTERNAL_SCHEMA, "");
  setFeatureOrWarn(factory, "http://apache.org/xml/features/disallow-doctype-decl", true);
  setFeatureOrWarn(factory, "http://xml.org/sax/features/external-general-entities", false);
  setFeatureOrWarn(factory, "http://xml.org/sax/features/external-parameter-entities", false);
  setFeatureOrWarn(factory, "http://apache.org/xml/features/nonvalidating/load-external-dtd", false);
  try {
    factory.setXIncludeAware(false);
    factory.setExpandEntityReferences(false);
  } catch (Exception e) {
    if (log.isWarnEnabled()) {
      log.warn("Caught " + e.getMessage() + " attempting to configure your XML parser.");
    }
  }
  return factory;
}

private static void setAttributeOrWarn(DocumentBuilderFactory factory, String attribute, String value) {
  try {
    factory.setAttribute(attribute, value);
  } catch (IllegalArgumentException e) {
    if (log.isWarnEnabled()) {
      log.warn(attribute + " property not supported by " + factory.getClass().getCanonicalName());
    }
  }
}

private static void setFeatureOrWarn(DocumentBuilderFactory factory, String feature, boolean value) {
  try {
    factory.setFeature(feature, value);
  } catch (ParserConfigurationException e) {
    if (log.isWarnEnabled()) {
      log.warn("FEATURE '" + feature + "' is probably not supported by " + factory.getClass().getCanonicalName());
    }
  }
}

