public static DocumentBuilderFactory newInstance(){
  DocumentBuilderFactory factory=DocumentBuilderFactory.newInstance();
  setFactoryProperty(factory, XMLConstants.ACCESS_EXTERNAL_DTD, "");
  setFactoryProperty(factory, XMLConstants.ACCESS_EXTERNAL_SCHEMA, "");
  setFactoryProperty(factory, "http://apache.org/xml/features/disallow-doctype-decl", true);
  setFactoryProperty(factory, "http://xml.org/sax/features/external-general-entities", false);
  setFactoryProperty(factory, "http://xml.org/sax/features/external-parameter-entities", false);
  setFactoryProperty(factory, "http://apache.org/xml/features/nonvalidating/load-external-dtd", false);
  setFactoryProperty(factory, "setXIncludeAware", false);
  setFactoryProperty(factory, "setExpandEntityReferences", false);
  return factory;
}

private static void setFactoryProperty(DocumentBuilderFactory factory, String property, Object value) {
  try {
    if (value instanceof String) {
      factory.setAttribute(property, value);
    } else if (value instanceof Boolean) {
      factory.setFeature(property, (Boolean) value);
    } else {
      // handle other types accordingly
    }
  } catch (Exception e) {
    if (log.isWarnEnabled()) {
      log.warn("Caught " + e.getMessage() + " attempting to configure your XML parser.");
    }
  }
}

