public static DocumentBuilderFactory newInstance(){
  DocumentBuilderFactory factory=DocumentBuilderFactory.newInstance();

  setFactoryProperty(factory, XMLConstants.ACCESS_EXTERNAL_DTD, "");
  setFactoryProperty(factory, XMLConstants.ACCESS_EXTERNAL_SCHEMA, "");
  setFactoryFeature(factory, "http://apache.org/xml/features/disallow-doctype-decl", true);
  setFactoryFeature(factory, "http://xml.org/sax/features/external-general-entities", false);
  setFactoryFeature(factory, "http://xml.org/sax/features/external-parameter-entities", false);
  setFactoryFeature(factory, "http://apache.org/xml/features/nonvalidating/load-external-dtd", false);

  try {
    factory.setXIncludeAware(false);
    factory.setExpandEntityReferences(false);
  }
 catch (  Exception e) {
    if (log.isWarnEnabled()) {
      log.warn("Caught " + e.getMessage() + " attempting to configure your XML parser.");
    }
  }
  return factory;
}

private static void setFactoryProperty(DocumentBuilderFactory factory, String propertyName, String value) {
  try {
    factory.setAttribute(propertyName, value);
  } catch (IllegalArgumentException e) {
    if (log.isWarnEnabled()) {
      log.warn(propertyName + " property not supported by " + factory.getClass().getCanonicalName());
    }
  }
}

private static void setFactoryFeature(DocumentBuilderFactory factory, String featureName, boolean value) {
  try {
    factory.setFeature(featureName, value);
  } catch (ParserConfigurationException e) {
    if (log.isWarnEnabled()) {
      log.warn("FEATURE '" + featureName + "' is probably not supported by " + factory.getClass().getCanonicalName());
    }
  }
}

